import React, { useState } from "react";
import { GoogleLogin, GoogleLogout, useGoogleLogin } from "react-google-login";
import UserProfile from "./UserProfile";
const clientId = process.env.REACT_APP_CLIENTID;
const redirectUri = process.env.REDIRECT_URI;
// added redirect for google api

function Login() {


  const [username, setusername] = useState("");
  const [useremail, setuseremail] = useState("");
  const [userimage, setuserimage] = useState("");

  const [showloginButton, setShowloginButton] = useState(true);
  const [showlogoutButton, setShowlogoutButton] = useState(false);
  const onLoginSuccess = (res) => {
    console.log("Login Success:", res.profileObj);
    setShowloginButton(false);
    setShowlogoutButton(true);
    setusername(res.profileObj.name);
    setuseremail(res.profileObj.email);
    setuserimage(res.profileObj.imageUrl);
    UserProfile.setEmail(res.profileObj.email);
    UserProfile.setName(res.profileObj.name);
    fetchEmail(res.profileObj.email);
  };

  const onLoginFailure = (res) => {
    console.log("Login Failed:", res);
  };

  const onSignoutSuccess = () => {
    alert("You have been logged out successfully");
    console.clear();
    setShowloginButton(true);
    setShowlogoutButton(false);
  };

  // useState for google api
  const { signIn, loaded } = useGoogleLogin({
    onSuccess: onLoginSuccess,
    clientId: clientId,
    onFailure: onLoginFailure,
    redirectUri: redirectUri,
  });

  function fetchEmail(email) {
    fetch("/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email: email }),
    });
  }

  return (
    <div>
      <div className="user-info">
        <img src={userimage} atl="" />
        <p>{username}</p>
        <p>{useremail}</p>
      </div>
      {showloginButton ? (
        // <GoogleLogin
        //   clientId={clientId}
        //   buttonText="Sign In"
        //   onSuccess={onLoginSuccess}
        //   onFailure={onLoginFailure}
        //   cookiePolicy={"single_host_origin"}
        //   isSignedIn={true}
        // />
        <button onClick={signIn}>Button</button>
      ) : null}

      {showlogoutButton ? (
        <GoogleLogout
          clientId={clientId}
          buttonText="Sign Out"
          onLogoutSuccess={onSignoutSuccess}
        ></GoogleLogout>
      ) : null}
    </div>
  );
}
export default Login;
